import { useState } from "react";
import Header from "./components/Header";
import Search from "./components/Search";
import GetData from "./components/GetData";

function App() {

  const [title , setTitle] = useState("Iron Man")

  const addTitle = (newTitle) => {
   setTitle(newTitle)
  }


  return (
    <div className="App">

    <Header />
    <Search onAdd = {addTitle}/>
    <GetData givenText= {title} />

    </div>
  );
}

export default App;
