import { useState,useEffect } from "react";
import Header from "./components/Header";
/*import Search from "./components/Search";
import GetData from "./components/GetData";
*/

function App() {

  const [title , setTitle] = useState("")
  const [container,setContainer] = useState([])

  /*const addTitle = (newTitle) => {
   setTitle(newTitle)
  }*/

 const display = () => 
  {
    container.map((item) => {
      return(
      <div>
        <p>{item.titleText.text}</p>
        <p>
          {
            typeof item.releaseYear.year === null ? "Not given" : item.releaseYear.year
          }
        </p>

      </div>
      )

    }

    )
  }

    const onSubmit = (e) => {

        e.preventDefault()
        display()

       // setTitle("")

    }

  useEffect(() => {fetchMe()},[title])

  const fetchMe = () => {

    const options = {
      method: 'GET',
      headers: {
          'X-RapidAPI-Key': '49eb6ba052msh1bc72a8983a318cp14adf1jsn06a9b26bddc8',
          'X-RapidAPI-Host': 'moviesdatabase.p.rapidapi.com'
      }
  }


  fetch(`https://moviesdatabase.p.rapidapi.com/titles/search/title/+${title}?exact=false`, options)
      .then(response => {return response.json()})
      .then(data => {setContainer(data.results)})
      .catch(err => console.error(err))
  }


  return (
    <div className="App">

    <Header />
    <form className="add-form" onSubmit= {onSubmit}>
    <div className="form-control">

            <input type="text" placeholder="Enter Title" value={title} onChange={(e) => setTitle(e.target.value)}/>
            <input type="submit" value="Search" className="btn btn-block"/>
    </div>
    </form>

     {
      container.map((item) =>{ 
      console.log(item.id)        
      return(
        <div>
          <p>{item.titleText.text}</p>
        </div>
        )

      })
     }
    </div>
  );
}

export default App;
