import { useState } from "react";
import Header from "./components/Header";
import Search from "./components/Search";
import GetData from "./components/GetData";

function App() {

  const [title , setTitle] = useState("Iron Man")

  const addTitle = (newTitle) => {
   setTitle(newTitle)
  }

  const display = (d) => {
    d.map((eachMovie) => { eachMovie.map((eachArr) => console.log(eachArr.titleText))})
  }


  return (
    <div className="App">

    <Header />
    <Search onAdd = {addTitle}/>
    <GetData givenText= {title} display={display}/>

    </div>
  );
}

export default App;
