import { useState,useEffect } from "react";
import Header from "./components/Header";


/*import Search from "./components/Search";
import GetData from "./components/GetData";
*/

function App() {

  const [title , setTitle] = useState("")
  const [container,setContainer] = useState([])
  const [finalKey, setFinalKey] = useState([])


  /*const addTitle = (newTitle) => {
   setTitle(newTitle)
  }*/


 /*const display = () => 
  {
    container.map((item) => {
      return(

      <div className="container">

        {JSON.stringify(item.releaseYear)
        //JSON.stringify(item)
        }

      </div>
      )

    }

    )
  }*/

    const onSubmit = (e) => {

        e.preventDefault()
        setFinalKey(container)


       // setTitle("")

    }

  useEffect(() => {fetchMe()},[title])

  const fetchMe = () => {

    const options = {
      method: 'GET',
      headers: {
          'X-RapidAPI-Key': '49eb6ba052msh1bc72a8983a318cp14adf1jsn06a9b26bddc8',
          'X-RapidAPI-Host': 'moviesdatabase.p.rapidapi.com'
      }
  }


  fetch(`https://moviesdatabase.p.rapidapi.com/titles/search/title/+${title}?exact=false`, options)
      .then(response => {return response.json()})
      .then(data => {setContainer(data.results)})
      .catch(err => console.error(err))
  }




  return (
    <div className="App">

    <Header />
    <form className="add-form" onSubmit= {onSubmit}>
    <div className="form-control">

            <input type="text" placeholder="Enter Title" value={title} onChange={(e) => setTitle(e.target.value)}/>
            <input type="submit" value="Search" className="btn btn-block"/>
    </div>
    </form>

    {
      finalKey.map((item,index) =>{ 
      //console.log(item.id)        
      return(
        <>
        <div key = {index} className="task">
          {console.log(item.primaryImage!==null ? item.primaryImage.url : "#")}
          <p><img src= {item.primaryImage!==null ? item.primaryImage.url : "#"} alt = "Movie" style = {{height : 500}} /></p>
          < a href = {`https://www.imdb.com/title/${item.id}/`} style = {{textDecoration: null}}><p>{item.titleText.text}</p></a>
          <p>Year of release: {item.releaseYear!==null ? item.releaseYear.year : "Not Given"}</p>

        </div>

        </>
        )

      })
     }
    </div>
  );
}

export default App;
