import { useState } from "react";
import Header from "./components/Header";
import Search from "./components/Search";

function App() {

  const [title , setTitle] = useState("Iron Man")

  const addTitle = (newTitle) => {
   setTitle(newTitle)
  }


  return (
    <div className="App">

    <Header />
    <Search onAdd = {addTitle}/>

    </div>
  );
}

export default App;
